Rules.InvariantScript:
With
(
    {
        fileJSON: JSON(
            FieldAttachFile.Image,
            JSONFormat.IncludeBinaryData
        )
    },

    Collect(
        colAttachedFiles,
        {
            fileName: ButtonAttachFile.FileName,
            fileExtension: Last(
                Split(
                    ButtonAttachFile.FileName,
                    "."
                )
            ).Result,
            fileContent: {
                contentType: Substitute(
                    First(
                        Split(
                            fileJSON,
                            ";"
                        )
                    ).Result,
                    """data:",
                    ""
                ),
                content: Substitute(
                    Last(
                        Split(
                            fileJSON,
                            ","
                        )
                    ).Result,
                    """",
                    ""
                )
            },
            blob: ButtonAttachFile.Media
        }
    );
);
